import { Component, OnInit } from '@angular/core';
import Book from '../../interfaces/book';
import { BookService } from 'src/app/services/book.service';
import { Router } from '@angular/router';
import { Observable } from 'rxjs';
import { BookState } from 'src/app/store/book.state';
import { Store, select } from '@ngrx/store';
import { loadBooks } from 'src/app/store/book.actions';

@Component({
  selector: 'app-books',
  templateUrl: './books.component.html',
  styleUrls: ['./books.component.css']
})
export class BooksComponent implements OnInit{
  public books: Book[] = [];
  public books$: Observable<Book[]>;

  constructor(private store: Store<BookState>, private router: Router){
    this.books$ = store.select('books');
  }

  ngOnInit(): void {
    // this.service.getAllBooks().subscribe( data => {
    //   this.books = data;
    // })
    this.store.dispatch(loadBooks());
    this.books$.subscribe( data => {
      this.books = data;
    })
  }

  showBook(id: number) {
    this.router.navigate([`/show-book/${id}`]);
  }

  updateBook(id: number) {
    this.router.navigate([`/update-book/${id}`]);
  }

  deleteBook(id: number) {
    this.router.navigate([`/delete-book/${id}`]);
  }
}
<h1>Books</h1>

<p *ngIf="!books">..Loading</p>

<table class="table table-striped">
  <thead>
    <th>Title</th>
    <th>Description</th>
    <th>Author</th>
    <th>Rate</th>
    <th>Date started</th>
    <th>Date read</th>
    <th>Status</th>
    <th>Action</th>
  </thead>
  <tbody>
    <tr *ngFor="let book of books">
      <td>{{ book.title }}</td>
      <td>{{ book.description | slice : 0 : 50 }}...</td>
      <td>{{ book.author }}</td>
      <td>{{ book.rate }}</td>
      <td>{{ book.dateStart | date : "dd/MM/yyyy" }}</td>
      <td>{{ book.dateRead | date : "dd/MM/yyyy" }}</td>
      <td>
        {{
          book.dateRead
            ? "Read"
            : book.dateStart
            ? "In Progress"
            : "Not Started"
        }}
      </td>
      <td>
        <button type="button" class="btn btn-info" (click)="showBook(book.id)">
          Details
        </button>
        <button type="button" class="btn btn-primary" (click)="updateBook(book.id)">
          Update
        </button>
        <button type="button" class="btn btn-danger" (click)="deleteBook(book.id)">
          Delete
        </button>
      </td>
    </tr>
  </tbody>
</table>
