import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { BookService } from 'src/app/services/book.service';

@Component({
  selector: 'app-new-book',
  templateUrl: './new-book.component.html',
  styleUrls: ['./new-book.component.css']
})
export class NewBookComponent implements OnInit {
  addBookForm!: FormGroup;
  showError: boolean = false;

  constructor(private service: BookService, private formBuilder: FormBuilder, private router: Router){}

  ngOnInit(): void {
    this.addBookForm = this.formBuilder.group({
      id: [Math.floor(Math.random()*1000)],
      title: [null, Validators.required],
      author: [null, Validators.required],
      description: [null, Validators.compose([Validators.required, Validators.minLength(30)])],
      rate: [null],
      dateStart: [null],
      dateRead: [null],
    })
  }

  onSubmit() {
    this.service.addBook(this.addBookForm.value).subscribe(data => {
      this.router.navigate(["/books"]);
    }, error => {
      this.showError = true;
    });
  }
}
<div class="add-book">
    <div *ngIf="showError" class="alert alert-danger" role="alert">
        Book was not added, Please try again!
    </div>
    <form [formGroup]="addBookForm" (ngSubmit)="onSubmit()">
        <div class="form-group">
            <label for="title" class="required">Title</label>
            <input type="text" class="form-control" name="title" id="title" formControlName="title" placeholder="Book Title">
        </div>
        <div class="form-group">
            <label for="description" class="required">Description</label>
            <input type="text" class="form-control" name="description" id="description" formControlName="description" placeholder="Book Description">
        </div>
        <div class="form-group">
            <label for="author" class="required">Author</label>
            <input type="text" class="form-control" name="author" id="author" formControlName="author" placeholder="Book Author">
        </div>
        <div class="row">
            <div class="col-md-4 col-xs-4 col-sm-4">
                <div class="form-group">
                    <label for="dateStart">Date Start</label>
                    <input type="date" class="form-control" name="dateStart" id="dateStart" formControlName="dateStart" placeholder="Date Start">
                </div>
            </div>
            <div class="col-md-4 col-xs-4 col-sm-4">
                <div class="form-group">
                    <label for="dateRead">Date End</label>
                    <input type="date" class="form-control" name="dateRead" id="dateRead" formControlName="dateRead" placeholder="Date Read">
                </div>
            </div>
            <div class="col-md-4 col-xs-4 col-sm-4">
                <div class="form-group">
                    <label for="rate">Rate</label>
                    <input type="number" min="0" max="5" class="form-control" name="rate" id="rate" formControlName="rate" placeholder="Book Rate">
                </div>
            </div>
        </div>
        <button type="submit" class="btn btn-success">Add Book</button>
        <button type="submit" class="btn btn-secondary" [routerLink]="['/books']">Cancel</button>
    </form>
</div>
