import { createReducer, on, Action } from '@ngrx/store';
import { loadBooksSuccess } from './book.actions';
import { BookState } from './book.state';

export const initialState: BookState = {
  books: [],
};
 
const _bookReducer= createReducer(initialState,
  on(loadBooksSuccess, (state, { books }) => ({ ...state, books }))
);
 
export function BookReducer(state: BookState | undefined, action: Action) {
  return _bookReducer(state, action);
}
